CREATE TABLE IF NOT EXISTS "core_escala" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "comentario" text NOT NULL, "e_escala_b" bool NOT NULL, "observacoes" text NOT NULL, "prevista" bool NOT NULL, "servico_id" integer NOT NULL REFERENCES "core_servico" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "core_trocaservico" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "data_troca" date NOT NULL, "data_solicitacao" datetime NOT NULL, "data_aprovacao" datetime NULL, "data_destroca" date NULL, "status" varchar(20) NOT NULL, "observacoes" text NOT NULL, "militar_solicitante_id" varchar(8) NOT NULL REFERENCES "core_militar" ("nim") DEFERRABLE INITIALLY DEFERRED, "militar_trocado_id" varchar(8) NOT NULL REFERENCES "core_militar" ("nim") DEFERRABLE INITIALLY DEFERRED);
